import types from './contact-types';
import { createAction } from '@reduxjs/toolkit';
import { v4 as uuidv4 } from 'uuid';

 export const addContacts = createAction(types.ADD, contactFormState => {
  return {
    payload: {
      id: uuidv4(),
      name: contactFormState.name,
      number: contactFormState.number,
    },
  };
});

export const deleteContacts = createAction(types.DELETE);
export const changeFilter = createAction(types.CHANGE_FILTER);

//export default { addContacts, deleteContacts, changeFilter };
